import 'react-native-gesture-handler';
import { View, Text, Image, StyleSheet, TextInput, Button,Modal, FlatList, TouchableOpacity } from 'react-native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer, useRoute } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';
import React, { useEffect, useState } from 'react';
import { getDatabase, ref as sRef, onChildRemoved, get } from 'firebase/database';

import OwnerEmployer from './Components/OwnerEmployer';
import Owner from './Components/Owner';
import LoginRestForm from './Components/LoginRestForm';
import Adminprincipal from './Components/Admin';
import TablesBut from './Components/Mozo';
import ChefBox from './Components/Chef';
import CajaBox from './Components/Caja';


const Stack = createNativeStackNavigator();


export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
      <Stack.Screen
          name="Own/employ"
          component={OwnerEmployer}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name="Owner"
          component={Owner}
          options={{ headerShown: false }}
        />
      <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{ headerShown: false }}
        />
      <Stack.Screen
          name="Dueño/Supervisor"
          component={AdminScreen}
          options={{ headerShown: false, gestureEnabled: false,}}
        />
        <Stack.Screen
          name="Adminprincipal"
          component={Adminprincipal}
          options={{ headerShown: false}}
        />
        <Stack.Screen
          name="Cocinero"
          component={ChefScreen}
          options={{ headerShown: false, gestureEnabled: false, }}
        />
        <Stack.Screen
          name="Mozo"
          component={WaiterScreen}
          options={{ headerShown: false, gestureEnabled: false, }}
        />
        <Stack.Screen
          name="Caja"
          component={CajaScreen}
          options={{ headerShown: false, gestureEnabled: false, }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

function HomeScreen() {
  return (
    <View style={styles.container}>
      <LoginRestForm/>
    </View>
  );
}

function RestInfo() {
  return (
    <View style={styles.container}>
      <RestForm/>
    </View>
  );
}

function AdminScreen({ route }) {
  const { nombre } = route.params;
  const navigation = useNavigation()
  return (
    <View style={{ flex: 1,}}>
      <View style={{backgroundColor: '#6CCCFC', flexDirection: 'row'}}>
      <TouchableOpacity style={styles.back} onPress={() => navigation.goBack()}><Text>Cerrar sesion</Text></TouchableOpacity>
      <Text style={{marginTop: 55, marginLeft: 'auto'}}>{nombre}  </Text>
      <Image
        source={require('./assets/images/user.png')}
        style={{ width:"8%", height:"35%", marginTop:"12%", marginRight:"3%"}}
      />
      </View>
      <Adminprincipal/>
    </View>
  );
}

function WaiterScreen({ route }) {
  const navigation = useNavigation();
  const { nombre } = route.params;
  const comandasRef = sRef(getDatabase(), 'comandas');
  const [modalVisible, setModalVisible] = useState(false);
  const [pedidoInfo, setPedidoInfo] = useState("nada aún");

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  const ModalComponent = ({ visible, onClose, message }) => {
    return (
      <Modal
        animationType="slide"
        transparent={true}
        visible={visible}
      >
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center',}}>
          <View style={{ backgroundColor: 'white', padding: 10, borderRadius: 10, borderColor: 'black', borderWidth: 1, }}>
            <Text>{message}</Text>
            <TouchableOpacity onPress={onClose} style={{ marginTop: 10, alignItems:"center" }}>
              <Text style={{color: "red", textAlign:"center"}}>Cerrar</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    );
  };

  onChildRemoved(comandasRef, (snapshot) => {
    // Obtener la comanda eliminada
    const comandaEliminada = snapshot.val();

    // Hacer algo con la comanda eliminada, como imprimir su información
    setModalVisible(true);
    setPedidoInfo(`El pedido de la mesa ${comandaEliminada.mesa} está listo para ser retirado`);
  });


  return (
    <View style={styles.container}>
      <View style={styles.navbar}>
      <TouchableOpacity style={styles.back} onPress={() => navigation.goBack()}><Text>Cerrar sesion</Text></TouchableOpacity>
      <Text style={{marginTop: 55, marginLeft: 'auto'}}>{nombre}  </Text>
      <TouchableOpacity style={{maxWidth:"20%", maxHeight:"10%",}} onPress={openModal}>
      <Image
        source={require('./assets/images/notification.png')}
        style={{ maxWidth:"70%", maxHeight:"500%", marginTop:"60%", marginRight:"3%"}}
      />
      </TouchableOpacity>
      <ModalComponent
        visible={modalVisible}
        onClose={closeModal}
        message={pedidoInfo}
      />
      </View>
      <TablesBut nombre={nombre}/>
    </View>
  );
}

function ChefScreen({ route }) {
  const navigation = useNavigation();
  const { nombre } = route.params;
  return (
    <View style={styles.container}>
      <View style={{backgroundColor: '#6CCCFC', flexDirection: 'row'}}>
      <TouchableOpacity style={styles.back} onPress={() => navigation.goBack()}><Text>Cerrar sesion</Text></TouchableOpacity>
      <Text style={{marginTop: 55, marginLeft: 'auto'}}>{nombre}  </Text>
      <Image
        source={require('./assets/images/user.png')}
        style={{ width:"8%", height:"35%", marginTop:"12%", marginRight:"3%"}}
      />
      </View>
      <ChefBox nombre={nombre}/>
    </View>
  );
}

function CajaScreen({ route }) {
  const navigation = useNavigation();
  const { nombre } = route.params;
  return (
    <View style={styles.container}>
      <View style={{backgroundColor: '#6CCCFC', flexDirection: 'row'}}>
      <TouchableOpacity style={styles.back} onPress={() => navigation.goBack()}><Text>Cerrar sesion</Text></TouchableOpacity>
      <Text style={{marginTop: 55, marginLeft: 'auto'}}>{nombre}  </Text>
      <Image
        source={require('./assets/images/user.png')}
        style={{ width:"8%", height:"35%", marginTop:"12%", marginRight:"3%"}}
      />
      </View>
      <CajaBox nombre={nombre}/>
    </View>
  );
}


const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    flex: 1,
  },
  logo: {
    width: 200,
    height: 150,
    marginTop: 60,
    marginBottom: 100,
    margin : '25%',
  },
  input: {
    width: '80%',
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 10,
    marginBottom: 20,
  },
  navbar: {
    backgroundColor: '#6CCCFC',
    flexDirection: 'row',
  },
  back: {
    marginLeft: 15,
    marginTop: 35,
    backgroundColor: '#6CCCFC',
    flex: 0,
    width: 100,
    height: 60,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  item: {
    marginBottom: 8,
  },
});
