            {opciones ? (
              opciones.map((opcion, index) => (
                <TouchableOpacity key={index} onPress={() => AgregarAlCarritoConVariante(item, opcion.nombre)}><Text>{opcion.nombre}</Text></TouchableOpacity>
              ))
            ) : null}
            <Button title="Cerrar" onPress={onClose} />
          </View>
        </View>
      </Modal>
    );
  };

  return (
    <View style={styles.white}>
      <Text style={styles.title}>{subcategoria}</Text>
        <FlatList
          style={{height:"100%"}}
