import { View, Text, StyleSheet, SafeAreaView, Image, TouchableOpacity, TextInput, Button} from 'react-native';
import { Appbar } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import React, { useState } from 'react';
import auth from '@react-native-firebase/auth';

export default function App() {
    return (
        <NavigationContainer independent={true}>
            <Stack.Navigator>
                <Stack.Screen
                name="form"
                component={Formulario}
                options={{ headerShown: false }}
                />
                <Stack.Screen
                name="register"
                component={RegisterScreen}
                options={{ headerShown: false }}
                />
                <Stack.Screen
                name="login"
                component={LoginScreen}
                options={{ headerShown: false }}
                />
        </Stack.Navigator>
        </NavigationContainer>
    );
}

function Formulario() {
    const navigation = useNavigation();

    return (
    <SafeAreaView style={styles.container}>
        <Image
            source={require('../assets/images/logo-restoapp.png')}
            style={styles.logo}
        />
        <Appbar.Header style={styles.line}>
            <Appbar.Content title="Elige una opción"/>
        </Appbar.Header>
        <View style={{flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: 'white',}}>

            <TouchableOpacity onPress={() => navigation.navigate("register")} style={styles.button}>
                <Text style={styles.buttonText}>Regitrarse</Text>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate("login")} style={styles.button}>
                <Text style={styles.buttonText}>Iniciar Sesion</Text>
            </TouchableOpacity>

        </View>
    </SafeAreaView>
    );
}


function LoginScreen({ navigation }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        try {
            await auth().signInWithEmailAndPassword(email, password);
            // Inicio de sesión exitoso, redirigir a la pantalla principal u otra pantalla de tu aplicación.
        } catch (error) {
            console.error('Error de inicio de sesión:', error);
        }
    };
    
    return (
        <View>
        <Text>Inicio de Sesión</Text>
        <TextInput
            placeholder="Correo electrónico"
            value={email}
            onChangeText={setEmail}
        />
        <TextInput
            placeholder="Contraseña"
            value={password}
            onChangeText={setPassword}
            secureTextEntry={true}
        />
        <Button title="Iniciar Sesión" onPress={handleLogin} />
        <Text onPress={() => navigation.navigate('register')}>
            ¿No tienes una cuenta? Regístrate aquí
        </Text>
        </View>
    );
}


function RegisterScreen() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    
    const handleRegister = async () => {
        try {
            await auth().createUserWithEmailAndPassword(email, password);
            // Registro exitoso, podrías redirigir al usuario a la pantalla de inicio de sesión.
        } catch (error) {
            console.error('Error de registro:', error);
        }
    };
    
    return (
        <View>
        <Text>Registro</Text>
        <TextInput
            placeholder="Correo electrónico"
            value={email}
            onChangeText={setEmail}
        />
        <TextInput
            placeholder="Contraseña"
            value={password}
            onChangeText={setPassword}
            secureTextEntry={true}
        />
        <Button title="Registrarse" onPress={handleRegister} />
        </View>
    );
}



const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor:"white",
    },
    logo: {
        width: 200,
        height: 150,
        marginTop: 0,
        marginBottom: 60,
        margin: "25%",
    },
    line: {
        backgroundColor: '#6CCCFC',
    },
    button: {
        width: 300,
        height: 100,
        backgroundColor: '#6CCCFC',
        borderWidth: 1,
        borderColor: 'black',
        borderRadius: 20,
        marginBottom: 80,
        justifyContent: 'center', 
        alignItems: 'center', 
    },
    buttonText: {
        fontWeight: 'bold',
        fontSize: 20,
    },
});
